insert  into `kpi_item_sql`(`item_code`,`item_name`,`item_type`,`if_stat`,`statement`,`statement_sum`,`ds_id`) values ('12.1','总GMV指标（万）(1-5)','2','Y','SELECT ROUND(SUM(coupon_gmv),2) FROM (\r\n# 1.卡券 GMV\r\nSELECT\r\n  SUM(o.amount) / 100 / 10000 AS \'coupon_gmv\' \r\nFROM hopsonone_coupons_v2.coupons_sub_order o,hopsonone_business.market m\r\nWHERE o.market_id = m.id\r\n  AND o.get_way = 3 \r\n  AND o.pay_dt >= \'$$BBRQQ$$\' AND o.pay_dt <= \'$$BBRQZ$$\' \r\nUNION ALL\r\n# 2.粮票领取记录计算的 粮票 GMV\r\nSELECT \r\n SUM(d.card_value) / 100 / 10000 AS \'foodCard_gmv(10k)\'\r\nFROM hopsonone_card.order_card_detail d,hopsonone_business.market m\r\nWHERE d.market_id = m.id AND d.market_id != 90000 AND d.channel != 2\r\n  AND d.create_time >= \'$$BBRQQ$$\' AND d.create_time <= \'$$BBRQZ$$\' \r\nUNION ALL\r\n# 3. 付费会员收益计算，包括type 4 单次购买，type 8 自动续费\r\nSELECT\r\n SUM(o.total_fee) / 100 / 10000 AS \'vip_gmv(10k)\'\r\nFROM\r\nhopsonone_order_center.orders o,\r\nhopsonone_business.market m\r\nWHERE o.market_id = m.id\r\n  AND o.order_type IN (4,8) \r\n  AND o.payment_dt >= \'$$BBRQQ$$\'  AND o.payment_dt <= \'$$BBRQZ$$\' \r\nUNION ALL\r\n# 4. 停车费用，只计算线上的停车费用\r\nSELECT\r\n SUM(o.total_fee) / 100 / 10000 AS \'park_gmv(10k)\'\r\nFROM\r\nhopsonone_park.orders o,\r\nhopsonone_business.market m\r\nWHERE o.market_id = m.id\r\n  AND o.order_type = 1\r\n  AND o.payment_dt >= \'$$BBRQQ$$\' AND o.payment_dt <= \'$$BBRQZ$$\' \r\nUNION ALL\r\n# 5. 电商 线上 GMV\r\nSELECT\r\n SUM(o.goods_amount_total) / 100 / 10000 AS \'mall_gmv(10k)\'\r\nFROM mall_order.order_sub o,hopsonone_business.market m\r\nWHERE o.market_id = m.id\r\n  AND o.create_time >= \'$$BBRQQ$$\' AND o.create_time <= \'$$BBRQZ$$\'  ) X\r\n\r\n','SELECT ROUND(SUM(coupon_gmv),2) FROM (\r\n# 1.卡券 GMV\r\nSELECT\r\n  SUM(o.amount) / 100 / 10000 AS \'coupon_gmv\' \r\nFROM hopsonone_coupons_v2.coupons_sub_order o,hopsonone_business.market m\r\nWHERE o.market_id = m.id\r\n  AND o.get_way = 3 \r\n  AND o.pay_dt >= \'2021-04-01 0:0:0\' AND o.pay_dt <= \'$$BBRQZ$$\' \r\nUNION ALL\r\n# 2.粮票领取记录计算的 粮票 GMV\r\nSELECT \r\n SUM(d.card_value) / 100 / 10000 AS \'foodCard_gmv(10k)\'\r\nFROM hopsonone_card.order_card_detail d,hopsonone_business.market m\r\nWHERE d.market_id = m.id AND d.market_id != 90000 AND d.channel != 2\r\n  AND d.create_time >= \'2021-04-01 0:0:0\' AND d.create_time <= \'$$BBRQZ$$\'\r\nUNION ALL\r\n# 3. 付费会员收益计算，包括type 4 单次购买，type 8 自动续费\r\nSELECT\r\n SUM(o.total_fee) / 100 / 10000 AS \'vip_gmv(10k)\'\r\nFROM\r\nhopsonone_order_center.orders o,\r\nhopsonone_business.market m\r\nWHERE o.market_id = m.id\r\n  AND o.order_type IN (4,8) \r\n  AND o.payment_dt >= \'2021-04-01 0:0:0\'  AND o.payment_dt <= \'$$BBRQZ$$\' \r\nUNION ALL\r\n# 4. 停车费用，只计算线上的停车费用\r\nSELECT\r\n SUM(o.total_fee) / 100 / 10000 AS \'park_gmv(10k)\'\r\nFROM\r\nhopsonone_park.orders o,\r\nhopsonone_business.market m\r\nWHERE o.market_id = m.id\r\n  AND o.order_type = 1\r\n  AND o.payment_dt >= \'2021-04-01 0:0:0\' AND o.payment_dt <= \'$$BBRQZ$$\' \r\nUNION ALL\r\n# 5. 电商 线上 GMV\r\nSELECT\r\n SUM(o.goods_amount_total) / 100 / 10000 AS \'mall_gmv(10k)\'\r\nFROM mall_order.order_sub o,hopsonone_business.market m\r\nWHERE o.market_id = m.id\r\n  AND o.create_time >= \'2021-04-01 0:0:0\' AND o.create_time <= \'$$BBRQZ$$\'  ) X\r\n\r\n',69);
