insert  into `kpi_item_sql`(`item_code`,`item_name`,`item_type`,`if_stat`,`statement`,`statement_sum`,`ds_id`) values ('7','支付即积分商户覆盖率','1','Y','SELECT\r\n concat(round(a.business_count / b.b_count*100,2),\'%\') as rate\r\nFROM\r\n(SELECT\r\n  d.market_id AS \'market_id\',\r\n  m.market_name AS \'market_name\',\r\n  COUNT(DISTINCT d.business_id) AS \'business_count\'\r\nFROM\r\nhopsonone_point.members_points_detail d,hopsonone_business.market m\r\nWHERE d.market_id = m.id\r\n  AND d.create_time >= \'$$BBRQQ$$\'  AND d.create_time <= \'$$BBRQZ$$\'  AND d.market_id in($$MARKET_ID$$)\r\n  AND d.state = 0 AND d.behavior_name IN (\'微信\', \'支付宝\')\r\nGROUP BY d.market_id) AS a \r\nLEFT JOIN\r\n(SELECT\r\n  b.market_id AS \'market_id\',\r\n  COUNT(DISTINCT b.business_id) AS \'b_count\'\r\nFROM hopsonone_business.business b\r\nWHERE b.market_id in($$MARKET_ID$$) AND b.`status` = 0 AND b.business_category = 1\r\nGROUP BY b.market_id\r\n) AS b ON a.market_id = b.market_id\r\n','SELECT\r\n concat(round(a.business_count / b.b_count*100,2),\'%\') as rate\r\nFROM\r\n(SELECT\r\n  d.market_id AS \'market_id\',\r\n  m.market_name AS \'market_name\',\r\n  COUNT(DISTINCT d.business_id) AS \'business_count\'\r\nFROM\r\nhopsonone_point.members_points_detail d,hopsonone_business.market m\r\nWHERE d.market_id = m.id\r\n  AND d.create_time >= \'2021-04-01 0:0:0\'  AND d.create_time <= \'$$BBRQZ$$\'  AND d.market_id in($$MARKET_ID$$)\r\n  AND d.state = 0 AND d.behavior_name IN (\'微信\', \'支付宝\')\r\nGROUP BY d.market_id) AS a \r\nLEFT JOIN\r\n(SELECT\r\n  b.market_id AS \'market_id\',\r\n  COUNT(DISTINCT b.business_id) AS \'b_count\'\r\nFROM hopsonone_business.business b\r\nWHERE b.market_id in($$MARKET_ID$$) AND b.`status` = 0 AND b.business_category = 1\r\nGROUP BY b.market_id\r\n) AS b ON a.market_id = b.market_id\r\n',69);
insert  into `kpi_item_sql`(`item_code`,`item_name`,`item_type`,`if_stat`,`statement`,`statement_sum`,`ds_id`) values ('8','保底积分率（积分销售额/总销售额）','1','Y','SELECT\r\n CONCAT(ROUND(a.point_amount / b.sales_report*100,2),\'%\') AS rate\r\nFROM\r\n(SELECT\r\nd.market_id AS \'market_id\',\r\nm.market_name AS \'market_name\',\r\nIFNULL(SUM(d.consume_amount),0) AS \'point_amount\'\r\nFROM\r\nhopsonone_point.members_points_detail d,\r\nhopsonone_business.market m\r\nWHERE\r\nd.market_id = m.id\r\nAND d.create_time >= \'$$BBRQQ$$\' AND d.create_time <= \'$$BBRQZ$$\'  AND d.market_id in($$MARKET_ID$$)\r\nAND d.state = 0\r\nGROUP BY d.market_id) AS a \r\nLEFT JOIN\r\n(SELECT\r\n b.market_id AS \'market_id\',\r\n SUM(d.adjust_tax_sales_amount_day) / 100 AS \'sales_report\'\r\nFROM shop_side_operation.sales_report_details d, hopsonone_business.business b\r\nWHERE d.business_id = b.business_id\r\nAND d.trade_date >= \'$$BBRQQ$$\'  AND d.trade_date <= \'$$BBRQZ$$\' AND b.market_id in($$MARKET_ID$$)\r\nGROUP BY b.market_id) AS b ON a.market_id = b.market_id\r\n','SELECT\r\n CONCAT(ROUND(a.point_amount / b.sales_report*100,2),\'%\') AS rate\r\nFROM\r\n(SELECT\r\nd.market_id AS \'market_id\',\r\nm.market_name AS \'market_name\',\r\nIFNULL(SUM(d.consume_amount),0) AS \'point_amount\'\r\nFROM\r\nhopsonone_point.members_points_detail d,\r\nhopsonone_business.market m\r\nWHERE\r\nd.market_id = m.id\r\nAND d.create_time >= \'2021-04-01 0:0:0\' AND d.create_time <= \'$$BBRQZ$$\'  AND d.market_id in($$MARKET_ID$$)\r\nAND d.state = 0\r\nGROUP BY d.market_id) AS a \r\nLEFT JOIN\r\n(SELECT\r\n b.market_id AS \'market_id\',\r\n SUM(d.adjust_tax_sales_amount_day) / 100 AS \'sales_report\'\r\nFROM shop_side_operation.sales_report_details d, hopsonone_business.business b\r\nWHERE d.business_id = b.business_id\r\nAND d.trade_date >= \'2021-04-01 0:0:0\'  AND d.trade_date <= \'$$BBRQZ$$\' AND b.market_id in($$MARKET_ID$$)\r\nGROUP BY b.market_id) AS b ON a.market_id = b.market_id\r\n',69);
insert  into `kpi_item_sql`(`item_code`,`item_name`,`item_type`,`if_stat`,`statement`,`statement_sum`,`ds_id`) values ('9','线下销售额','1','Y','SELECT\r\nround(sum(d.adjust_tax_sales_amount_day) / 100 / 10000,2)\r\nFROM\r\nshop_side_operation.sales_report_details d,hopsonone_business.business b\r\nWHERE d.business_id = b.business_id\r\n  and d.trade_date >= \'$$BBRQQ$$\' and d.trade_date <= \'$$BBRQZ$$\' and b.market_id in($$MARKET_ID$$)','SELECT\r\nround(sum(d.adjust_tax_sales_amount_day) / 100 / 10000,2)\r\nFROM\r\nshop_side_operation.sales_report_details d,hopsonone_business.business b\r\nWHERE d.business_id = b.business_id\r\n  and d.trade_date >= \'2021-04-01 0:0:0\' and d.trade_date <= \'$$BBRQZ$$\' and b.market_id in($$MARKET_ID$$)',69);
